ruleToc.nextTocUrl:
text.下一页@href
@js:
var url=[],
next=result[0];
em=java.getString("em#pagestats@textNodes");
page=em.match(/\d+/);
ys=page?page[0]:null;
if(ys>2){
	for(i=2;i<=ys;i++){
		link=String(next).
		replace(/\d+.asc/,
			i+"/asc");
			url.push(link)
		}
	}else{url=result}
url;

