.select('.nav li a');
var arr=[],sort=[];
if(html&&a){
	for(i in a){
		name=a[i].text()
		href=a[i].attr("href")
		link=String(href).replace(/1/,"{{page}}")
		add=[name,link];
		arr.push(add)
		}
		sort=arr.map(([title,link])=>({
    "title": title,
    "url": link,
    "style": {
      "layout_flexGrow": 1,
       "layout_flexBasisPercent": 0.2
    }
  }));

  JSON.stringify(sort)
	}else{
		java.toast("发现列表获取失败！")
		}

