loginUrl:
@js:
function login() {
  let une = source.getLoginInfoMap().get("账号")
  let pwd = source.getLoginInfoMap().get("密码")

  if (une && pwd) {
    let body = JSON.stringify({
      "username": String(une),
      "password": String(pwd),
      "isAjax": 1
    })
    let url = source.bookSourceUrl + 'index.php/home/login/signin'
    let ck = java.post(url, body, { "Content-Type": "application/json" }).cookies()

    let header = JSON.stringify({
      "Cookie": String(ck).match(/\{(.*?)\}/)[1].replace(/,/g, ';')
    })
    source.putLoginHeader(header)
  }
}

